import { animated, useSpring } from "@react-spring/web";
import { useEffect } from "react";

const Toast = ({isVisible}:{isVisible:boolean}) => {
    const [springs, api] = useSpring(() => ({
        opacity: isVisible ? 1 : 0,
        transform: isVisible ? "scale(1)" : "scale(0.9)",
        config: { tension: 1, friction: 20 },
      }));

      useEffect(() => {
          api.start({ opacity: 1 });

      }, [api])

    return (
        <animated.div
        className="fixed inset-0 bg-[rgba(0,0,0,0.6)] flex justify-center items-center z-50"
        style={{ ...springs }}
      >
        {/* <div className="bg-white rounded-xl p-6 shadow-xl text-center">
          <h2 className="text-xl font-bold mb-2">Header</h2>
          <p className="text-gray-600">This is a smooth toast message.</p>
        </div> */}
      </animated.div>
    )
}

export default Toast
