import React from 'react'
import { timeAgo } from '../utils/functions'

interface MessageProps {
    time: string,
    text?: string,
    isOnLeft: boolean,
    isLast: boolean,
    fileObject?: FileObject
}

const MessageFabric: React.FC<MessageProps> = ({ time, text, isOnLeft, isLast, fileObject }) => {

    if (true) return <FileMessage time={time} isOnLeft={isOnLeft} isLast={isLast} fileObject={fileObject}/>
    return <TextMessage time={time} isOnLeft={isOnLeft} isLast={isLast} text={text}/>
}   

export default MessageFabric

const TextMessage: React.FC<MessageProps> = ({ time, text, isOnLeft, isLast }) => {
    return (
        <li className={`mt-1 mb-1 p-[8px] ${isOnLeft ? "" : "flex items-end"} flex-col ${isLast && 'pb-10'}`} >
                <section className={`${isOnLeft ? "bg-white text-black" : "bg-black text-white "} p-2 pl-5 pr-5 rounded-3xl inline-block max-w-[400px] break-words text-[25px] shadow-xl`}>
                    {text}
                </section>
                <section className={`text-[14px] font-black text-bold pl-3 pr-3 ${isOnLeft ? "" : "flex justify-end"}`}>
                    {timeAgo(time)}
                </section>

        </li>
    )
}

