import { animated, useSpring } from "@react-spring/web";
import { useEffect } from "react";

const Toast = () => {
    const [springs, api] = useSpring(() => ({
        opacity: 0,
        config: { tension: 1, friction: 0.1 },
      }));

      useEffect(() => {
          api.start({ opacity: 1 });

      }, [api])

    return (
        <animated.div className="absolute w-full h-full bg-[rgba(0,0,0,0.5)] flex flex-column justify-center items-center" style={{...springs}}>
            <div className="bg-white rounded-xl flex flex-column justify-center items-center">
                <a>Header</a>
                <p>Text</p>
            </div>
        </animated.div>
    )
}

export default Toast
