import React, { useEffect, useState } from 'react'
import { timeAgo } from '../utils/functions'
import { iconsRef } from '../utils/iconsRef'
import gif from '/assets/gifs/clocksWhite.gif'
import { useMessenger } from '../context/MessengerContext'
import { FileEntry } from '../types'

interface MessageProps {
    time: string,
    text?: string,
    isOnLeft: boolean,
    isLast: boolean,
    fileEntry?: FileEntry
}

const MessageFabric: React.FC<MessageProps> = ({ time, text, isOnLeft, isLast, fileEntry }) => {

    if (fileEntry) return <FileMessage time={time} isOnLeft={isOnLeft} isLast={isLast} fileEntry={fileEntry} />
    return <TextMessage time={time} isOnLeft={isOnLeft} isLast={isLast} text={text} />
}

export default MessageFabric

const TextMessage: React.FC<MessageProps> = ({ time, text, isOnLeft, isLast }) => {
    return (
        <li className={`mt-1 mb-1 p-[8px] ${isOnLeft ? "" : "flex items-end"} flex-col ${isLast && 'pb-10'}`} >
            <section className={`${isOnLeft ? "bg-white text-black" : "bg-black text-white "} p-2 pl-5 pr-5 rounded-3xl inline-block max-w-[400px] break-words text-[25px] shadow-xl`}>
                {text}
            </section>
            <section className={`text-[14px] font-black text-bold pl-3 pr-3 ${isOnLeft ? "" : "flex justify-end"}`}>
                {timeAgo(time)}
            </section>

        </li>
    )
}

const FileMessage: React.FC<MessageProps> = ({ time, isOnLeft, isLast, fileEntry }) => {
    const {expiredFiles} = useMessenger()
    const [expired, setExpired] = useState(false)

    useEffect(() => {
        if (fileEntry && expiredFiles.includes(fileEntry.filename)) {
            setExpired(true)
        }
    }, [expiredFiles])

    // stupid TS hate you bitch can you see that prewvious if statement? bitch
    if (fileEntry)

        return (
            <li className={`mt-1 mb-1 p-[8px] ${isOnLeft ? "" : "flex items-end"} flex-col ${isLast && 'pb-10'}`} >
                <section className={`${isOnLeft ? "bg-white text-black" : "bg-black text-white "} flex flex-row items-center justify-between p-2 pl-5 pr-5 rounded-3xl inline-block max-w-[400px] break-words text-[25px] shadow-xl relative`}>
                    <div className='flex flex-row'>
                        <Icon icon={isOnLeft ? iconsRef.fileBlack : iconsRef.fileWhite} />
                        <div className='flex flex-col'>
                            <a>{fileEntry.originalFilename}</a>
                            <a className={`text-[15px] ${isOnLeft ? 'text-[#5c5c5c]' : 'text-[#c9c9c9]'}`}>{fileEntry.size}</a>
                        </div>
                    </div>
                    {isOnLeft && !expired ?
                        <img src={iconsRef.clocksBlack} alt="Descriptive text" width={25} height={25} className='ml-2 align-self-end justify-self-end absolute right-2 bottom-2' />


                        :
                        <img src={iconsRef.clocksWhite} alt="Descriptive text" width={25} height={25} className='ml-2 align-self-end justify-self-end absolute right-2 bottom-2' />


                    }


                </section>
                <section className={`text-[14px] font-black text-bold pl-3 pr-3 ${isOnLeft ? "" : "flex justify-end"}`}>
                    {timeAgo(time)}
                </section>

            </li>
        )
    else return <li></li>
}

interface FileObject {
    size: string
    filename: string
    fileId: string
    sender?: string
    receiver?: string
}

const Icon = ({ icon, w = 50, h = 50 }: { icon: string; w?: number; h?: number }) => {
    return (
        <img
            src={icon}
            width={w}
            height={h}
            style={{ cursor: 'pointer' }}
        />
    );
};
