import * as CryptoJS from 'crypto-js';
import { MessageInterface } from '../types';


const P = BigInt(23)
const g = BigInt(2)



export const DiffieHellmanService = {
    generateSecret() {
        const randomKey = BigInt(Math.floor(Math.random() * Number(P - g) + 1));
        return randomKey;
    },

    modularExponentiation(base: bigint, exponent: bigint, modulus: bigint) {
        if (modulus === BigInt(1)) return BigInt(0);
        let result = BigInt(1);
        base = base % modulus;
        while (exponent > 0) {
            if (exponent % BigInt(2) === BigInt(1)) {
                result = (result * base) % modulus;
            }
            exponent = exponent >> BigInt(1);
            base = (base * base) % modulus;
        }
        return result;
    },

    generatePublicKey(secret: bigint) {
        const pubKey = this.modularExponentiation(g, secret, P);
        return pubKey;
    },

    generateSharedSecret(otherPartyPublicKey: string, ownPrivateKey: string) {
        if (!otherPartyPublicKey || !ownPrivateKey) return null;
        return this.modularExponentiation(BigInt(otherPartyPublicKey), BigInt(ownPrivateKey), P);
    },

    handleGenerateKeys() {
        var secret = this.generateSecret();
        const publicKey = String(this.generatePublicKey(secret));
        console.log("Private Key (Secret):", secret.toString());
        console.log("Public Key:", publicKey.toString());
        return { secret, publicKey };
    },

    encrypt(plainText: string, secretKey: string) {
        const cipherText = CryptoJS.AES.encrypt(plainText, secretKey).toString()
        return cipherText
    },

    decrypt(cipherText: string, secretKey: string) {
        const bytes = CryptoJS.AES.decrypt(cipherText, secretKey)
        const plainText = bytes.toString(CryptoJS.enc.Utf8)
        return plainText
    },

    decryptMessages(messages: MessageInterface[], secretKey: string) {

        const decryptedMessages = messages.map((msg) => {
          if (!msg.content) return {}

            msg.content = this.decrypt(msg.content, secretKey)
            return msg
        })

        return decryptedMessages
    },

    async encryptFile(file: File, secretKey: string): Promise<File> {
        const reader = new FileReader();
        return new Promise((resolve, reject) => {
          reader.onload = () => {
            try {
              const fileData = reader.result as string;
              const encryptedData = this.encrypt(fileData, secretKey);

              const encryptedBlob = new Blob([encryptedData], { type: file.type });
              const encryptedFile = new File([encryptedBlob], `${file.name}.enc`, {
                type: file.type,
              });

              resolve(encryptedFile);
            } catch (error) {
              reject(error);
            }
          };

          reader.onerror = (error) => reject(error);
          reader.readAsText(file);
        });
      },

      async encryptImage(file: File, secretKey: string): Promise<string> {
        try {
          // Convert File to base64 string
          const base64String = await new Promise<string>((resolve, reject) => {
            const reader = new FileReader();
            reader.onload = () => {
              if (typeof reader.result === 'string') {
                resolve(reader.result);
              } else {
                reject(new Error('Failed to read file'));
              }
            };
            reader.onerror = reject;
            reader.readAsDataURL(file);
          });

          // Encrypt the base64 string
          const encrypted = CryptoJS.AES.encrypt(base64String, secretKey).toString();
          return encrypted;
        } catch (error) {
          console.error('Error encrypting image:', error);
          throw new Error('Failed to encrypt image');
        }
      },
      encryptImage(imageData: string, secretKey: string): string {
        try {
          // Remove data URL prefix if present
          const base64Data = imageData.includes('data:image') 
            ? imageData.split(',')[1] 
            : imageData;

          // Encrypt the base64 string
          const encrypted = CryptoJS.AES.encrypt(base64Data, secretKey).toString();
          return encrypted;
        } catch (error) {
          console.error('Error encrypting image:', error);
          throw new Error('Failed to encrypt image');
        }
      },
      decryptImage(encryptedData: string, secretKey: string): string {
        try {
          // Decrypt the data
          const decrypted = CryptoJS.AES.decrypt(encryptedData, secretKey);
          const base64 = decrypted.toString(CryptoJS.enc.Utf8);

          // Add data URL prefix if it's not present
          if (!base64.includes('data:image')) {
            // You might want to make this dynamic based on image type
            return `data:image/jpeg;base64,${base64}`;
          }
          return base64;
        } catch (error) {
          console.error('Error decrypting image:', error);
          throw new Error('Failed to decrypt image');
        }
      },
      fileToBase64(file: File): Promise<string> {
        return new Promise((resolve, reject) => {
          const reader = new FileReader();
          reader.readAsDataURL(file);
          reader.onload = () => resolve(reader.result as string);
          reader.onerror = error => reject(error);
        });
      }


}

export default DiffieHellmanService;
