import { useMessenger } from "../context/MessengerContext"
import Chat from "./Chat"
import LeftBar from "./LeftBar"
import MessageInput from "./MessageInput"

const Body = () => {
    const { isMobile, showSidebar, currentChat } = useMessenger()

    return (
        <div className="flex flex-row bg-[#ebebeb]">
            {(!isMobile || showSidebar) && <LeftBar />}
            {true ?
                <div className='w-screen flex-col h-screen'>
                    <Chat />
                    <MessageInput />
                </div> : <div>Select a chat to start messaging</div>}
            {/* <button onClick={() => setRefreshTrigger(refreshTrigger + 1)}>Refresh</button> */}

        </div>
    )
}

export default Body
