import { useContext, useState } from 'react';
import { useMessenger } from '../context/MessengerContext';
import AnimatedButton from './Button';
import { getOpponentNickname } from '../utils/functions';
import { MessengerService } from '../api/MessengerService';
import { MessageInterface } from '../types';
import { useUser } from '../context/UserContext';
import { iconsRef } from '../utils/iconsRef';


const MessageInput = ({ updateMessages }: any) => {
  const [message, setMessage] = useState('');
  const { user } = useUser()
  const { messages, setMessages, chatWidth, currentChat } = useMessenger()

  const sendMessage = async () => {
    if (!user || !currentChat) return
    if (message.trim()) {
      setMessage('');
      await MessengerService.sendMessage(getOpponentNickname(user, currentChat), message, String(currentChat.sharedSecretKey), user.jwt)
    }
    const messageToSave: MessageInterface =
    {
      sender: user.nickname,
      receiver: getOpponentNickname(user, currentChat),
      time: String(new Date()),
      content: message
    }
    setMessages((prev) => [...prev, messageToSave])
  };

  return (
    <div className="flex items-center p-4 h-[100px] absolute bottom-0 justify-center" style={{ width: chatWidth, zIndex: 500 }}>
      <div className='flex' style={{ width: chatWidth * 0.7 }}>
        <div className="flex flex-row p-2 border border-[#4f4f4f] bg-white rounded mr-2 bg-transparent w-full " >
          <input
            className='w-full text-black focus:outline-none focus:ring-0'
            type="text"
            placeholder="Type your message..."
            value={message}
            onChange={(e) => setMessage(e.target.value)}
          />
          <div className='flex flex-row gap-2 max-h-[40px]'>
            <Icon icon={iconsRef.photo} onClick={() => console.log('fuck')} />
            <Icon icon={iconsRef.clip} onClick={() => console.log('fuck')} />
          </div>

        </div>


        <AnimatedButton text={'Send'} onClick={sendMessage} />


      </div>

    </div>
  );
};

const Icon = ({ icon, onClick, w = 30, h = 30, }: { icon: string, onClick: () => void }) => {

  return (
    <img
      src={icon}
      width={w}
      height={h}
      onClick={onClick}
      style={{ cursor: 'pointer' }}
    />
  );
};

export default MessageInput;
