import { Key } from "react"
import { useMessenger } from "../context/MessengerContext"
import { MessageInterface } from "../types"
import Chat, { templateMsgs } from "./Chat"
import LeftBar from "./LeftBar"
import Message from "./Message"
import MessageInput from "./MessageInput"

const Body = () => {
    const { isMobile, showSidebar, currentChat, screenSize } = useMessenger()

    return (
        <div className="flex flex-row bg-[#ebebeb] w-full h-full">
            {(!isMobile || showSidebar) && <LeftBar />}

            <ul className="overflow-auto w-full"
             style={{
                maxHeight: `${screenSize.height - 100}px`,
                height: `${screenSize.height - 100}px`,
              }}
            >
                {templateMsgs.map((message: MessageInterface, index: Key) => (
                    <Message
                        key={index}
                        time={message.time}
                        text={message.content}
                        isOnLeft={'Bob' === message.sender}
                    />
                ))}
            </ul>
        </div>
    )
}

export default Body
