import React, { useEffect, useState } from 'react';
import { animated, useSpring, config } from '@react-spring/web'
import { MessengerService } from '../api/MessengerService';
import { useUser } from '../context/UserContext';
import { ChatInterface } from '../types';
import { useMessenger } from '../context/MessengerContext';
import { getOpponentNickname } from '../utils/functions';
import SearchBar from './animated/searchBar/SearchBar';
import ChatBlock from './animated/chatBlock/ChatBlock';

const LeftBar: React.FC<{ isVisible: boolean }> = ({ isVisible }) => {
  const { user } = useUser();
  const { chats, setCurrentChat, isMobile, screenSize } = useMessenger();
  const [searchTerm, setSearchTerm] = useState('');
  const [showedChats, setShowedChats] = useState<ChatInterface[]>([]);

  // Animation for mount and dismount
  const springs = useSpring({
    from: { x: -250, opacity: 0 },
    to: { 
      x: isVisible ? 0 : -250, 
      opacity: isVisible ? 1 : 0 
    },
    config: config.gentle
  });

  useEffect(() => {
    if (chats) {
      setShowedChats(chats);
    } else {
      setShowedChats([]);
    }
  }, [chats]);

  async function handleSendingRequest() {
    if (!user) return;
    const response = await MessengerService.sendChatRequest(
      { requestedNickname: searchTerm, requesterPublicKey: user.publicKey },
      user.jwt
    );
    if (response === 'success') {
      setSearchTerm('');
    }
  }

  const renderChats = () => {
    return (
      showedChats.map((chat: ChatInterface) => (
        <li
          onClick={() => setCurrentChat(chat)}
          style={{ cursor: 'pointer', padding: '5px 0' }}
        >
          {getOpponentNickname(user, chat)}
        </li>
      ))
    );
  }

  // Only render if there's something to show
  if (!isVisible && showedChats.length === 0) return null;

  return (
    <animated.div 
      className={`w-[250px] p-2 border-r border-gray-300 flex flex-col items-center bg-white shadow-xl ${isMobile ? 'absolute' : 'h-full'}`}
      style={{
        maxHeight: `${screenSize.height - 80}px`,
        height: `${screenSize.height - 80}px`,
        zIndex: 999,
        position: 'fixed',
        top: 0,
        left: 0,
        ...springs,
      }}
    >
      <SearchBar />
      <div className="w-full overflow-auto">
        {renderChats()}
        <ChatBlock nickname="MyFriend2943" />
        <ChatBlock nickname="SuperPimkpin233" />
        <ChatBlock nickname="DearBroad233" />
      </div>
    </animated.div>
  );
};

// Wrapper component to manage visibility
const LeftBarWrapper: React.FC = () => {
  const { showSidebar } = useMessenger();

  return showSidebar ? <LeftBar isVisible={true} /> : null;
};

export default LeftBarWrapper;
