    const {isMobile, showSidebar, currentChat} = useMessenger()

    return (
        <div style={{ display: 'flex', flex: 1 }}>
            {(!isMobile || showSidebar) && <LeftBar/>}
             {true ?   
             <section className='w-[75%] h-full flex-col'>
                            <div className='h-5/6'>
                                <Chat />
                            </div>
                            <div className='h-1/6'>
                                {/* <MessageInput updateMessages={updateMessages} /> */}
                            </div>
                        </section> : <div>Select a chat to start messaging</div>}
            {/* <button onClick={() => setRefreshTrigger(refreshTrigger + 1)}>Refresh</button> */}

       </div>
    )
}

export default Body
